import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

function App() {
  const [items, setItems] = useState([]);
  const [limit, setLimit] = useState();
  const [noOfElement, setnoOfElement] = useState(8);

  const loadMore = () => {
    setnoOfElement(noOfElement + 8);
  };

  const slice = items.slice(0, noOfElement);

  useEffect(() => {
    fetch("https://picsum.photos/v2/list?page=1&limit=100")
      .then((res) => res.json())
      .then((data) => setItems(data));
  });

  return (
    <Router>
      <div className="Full">
        <div className="LoGo">
          <img></img>
          <div className="Header">
            <h1>Picturism</h1>
            <div className="container">
              {slice.map((item) => (
                <div key={items.id} className="column">
                  <img className="img" src={item.download_url} alt="img"></img>
                  {/* <p className='id'>Picture : {item.id} </p>
                  <p className='author'>author : {item.author} </p> */}
                </div>
              ))}
              <div className="btn">
                <button className="btn-dark" onClick={() => loadMore()}>
                  Load More!
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </Router>
  );
}

export default App;
