import React, { useEffect, useState } from "react";

function App() {
  const [items, setItems] = useState([]);
  const [limit, setLimit] = useState();
  const [noOfElement, setnoOfElement] = useState();

  const loadMore = () => {
    setnoOfElement(noOfElement + 4);
  };

  const slice = items.slice(0, noOfElement);

  useEffect(() => {
    fetch("https://picsum.photos/v2/list?page=1&limit=300")
      .then((res) => res.json())
      .then((data) => setItems(data));
  });

  return (
    <div className="App">
      <h1>hello</h1>
      <div className="container">
        {slice.map((item) => (
          <div key={items.id} className="">
            <img className="img" src={item.download_url} alt="img" style="max-width: 100%"></img>
            {/* <p className='id'>Picture : {item.id} </p>
                  <p className='author'>author : {item.author} </p> */}
          </div>
        ))}

        <button className="btn btn-dark" onClick={() => loadMore()}>
          Load More!
        </button>
      </div>
    </div>
  )
}

export default App;
