import React , {useEffect, useState} from 'react'


function App() {

  const [items  , setItems]= useState([]);
  const [limit , setLimit] = useState();
  const [noOfElement , setnoOfElement] = useState()

 const LoadMore = () => {

    useEffect(()=>{
      fetch('https://picsum.photos/v2/list?page=2&limit=300')
      .then((res)=>res.json())
      .then((data)=>setItems(data))
    })

   }


  const slice =  items.slice(0 , noOfElement);

  useEffect(()=>{
    fetch('https://picsum.photos/v2/list?page=1&limit=300')
    .then((res)=>res.json())
    .then((data)=>setItems(data))
  })


  return (
    <div className="App">
        <h1>hello</h1>
      <div className='container'>

           {slice.map(item=>(

              <div key={items.id} className=''>
                  <img className= 'img' src = {item.download_url}></img>
                  {/* <p className='id'>Picture : {item.id} </p>
                  <p className='author'>author : {item.author} </p> */}
                 </div> 

            ))}


      <button className='btn btn-dark' onClick = {() => LoadMore() }>
        Load More!
      </button>



      </div>

    </div> 
  ) 
}




export default App;
